// Helper function to convert JSON object to markdown string
function jsonToMarkdown(jsonObj) {
    let markdown = '';

    // Keys to exclude from markdown conversion
    const excludedKeys = ['organization_id', 'project_id', 'id', 'document_id', 'block_id'];

    for (const [key, value] of Object.entries(jsonObj)) {
        // Skip excluded keys
        if (excludedKeys.includes(key)) {
            continue;
        }

        markdown += `**${key}**: `;

        if (typeof value === 'string') {
            markdown += `${value}\n`;
        } else if (typeof value === 'number' || typeof value === 'boolean') {
            markdown += `${value}\n`;
        } else if (value === null) {
            markdown += `null\n`;
        } else if (typeof value === 'object') {
            markdown += `\n${JSON.stringify(value, null, 2)}\n`;
        } else {
            markdown += `${value}\n`;
        }
    }

    return markdown;
}

// Collect all markdown strings from each item.json
let allDocumentsMarkdown = '';

for (const item of $input.all()) {
    // Convert each item.json to markdown
    const markdownString = jsonToMarkdown(item.json);

    // Add to the combined string with double newlines between items
    if (allDocumentsMarkdown !== '') {
        allDocumentsMarkdown += '\n\n';
    }
    allDocumentsMarkdown += markdownString;
}

// Return new array with single object containing all documents as markdown
return [{"all_documents": allDocumentsMarkdown}];

