// Loop over input items and extract JSON from output field
const results = [];

for (const item of $input.all()) {
  // Extract JSON content from the output field
  const outputText = item.json.output;

  // Regular expression to extract content between ```json and ```
  const jsonMatch = outputText.match(/```json\n([\s\S]*?)\n```/);

  if (jsonMatch) {
    try {
      // Parse the JSON string
      const parsedJson = JSON.parse(jsonMatch[1]);

      // If it's an array, add myNewField to each item
      if (Array.isArray(parsedJson)) {
        for (const jsonItem of parsedJson) {
          jsonItem.myNewField = 1;
        }
        results.push(...parsedJson);
      } else {
        // If it's a single object
        parsedJson.myNewField = 1;
        results.push(parsedJson);
      }
    } catch (error) {
      console.error('JSON parsing error:', error);
    }
  }
}

return results;

